hudson.tasks.Shell.command:
#!/bin/bash
sudo su - root BASEPATH=''
APPCODE='/geppetto/generated-code/kishan/app/client/desktop' SYSTEMENTRYIMAGENAME='tharanirajan/kishan-app:1.0'
SYSTEMENTRYIMAGE=$(sudo docker images | awk '{ print $1,$2 }' | grep tharanirajan/kishan-app | awk '{print $1 }')
echo "Runing....."
update_code () {
cd $BASEPATH
git pull if [ $? -eq 0 ]; then echo "Code updated sucessfully....." else echo "git pull failed!" fi
}
delete_if_existing_app () {
if [ ! "$SYSTEMENTRYIMAGE" ]; then echo "kishan-app:1.0 Image is not available" else echo "Deleting kishan-app:1.0 Image" sudo docker rmi -f $SYSTEMENTRYIMAGENAME echo "Deleted...." fi
}
build_and_push_image_app () {
cd $APPCODE if [ $? -eq 0 ]; then sudo docker build -t $SYSTEMENTRYIMAGENAME . if [ $? -eq 0 ]; then echo "image build sucessfully" sudo docker push $SYSTEMENTRYIMAGENAME if [ $? -eq 0 ]; then echo "image kishan-app:1.0 pushed sucessfully" else echo "image kishan-app:1.0 push failed" fi else echo "kishan-app:1.0 image build failed" fi else echo "ng build failed!" fi }
#update_code delete_if_existing_app build_and_push_image_app
